document.addEventListener('DOMContentLoaded',()=>{const cards=document.querySelectorAll('.service-card');cards.forEach((card,index)=>{setTimeout(()=>{card.classList.add('animate');},index*200);});});;function openModal(content){const modal=document.getElementById('modal');const modalContent=document.getElementById('modal-content');modalContent.innerHTML=content;modal.style.display='block';}
function closeModal(){const modal=document.getElementById('modal');modal.style.display='none';}
window.onclick=function(event){const modal=document.getElementById('modal');if(event.target===modal){closeModal();}};;